import React ,{useState} from 'react';

const App = () => {
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)

  const setGoodValue = (value) =>{
    setGood(value);
  }

  const setNeutralValue = (value) =>{
    setNeutral(value);
  }

  const setBadValue = (value) =>{
    setBad(value);
  }

  return (
    <div >
      <Header header = "Unicafe feedback" />
      <GoodButton good={good} handleClick = {setGoodValue} text = "good" />
    </div>
  );
}


const Header = ( {header} ) =>( <p>{header}</p> )
const GoodButton = ({good, setGoodValue, text}) => {
  return (<button onClick = {() => setGoodValue(good+1)}>{text}</button>)
}
export default App;
